import React from "react";
import "./App.css";
import {loadNavermapsScript, NaverMap} from 'react-naver-maps';
//import { RenderAfterNavermapsLoaded, NaverMap } from 'react-naver-maps'; // 패키지 불러오기

class Map extends React.Component {
     // map = new naver.maps.Map('mapDiv', {option});
     constructor(props){
        super(props);
        const navermaps = window.naver.maps;

        this.state = {
            map: navermaps,
            input_latlng: '',
            zoomControl : true,
            zoom:2,
            mapTypeId: 'normal',
            center : navermaps.LatLng(36.4800580, 127.289039), // 126.9861108, 37.4983439
            
            zoomControlOptions: {
                center: new navermaps.LatLng(36.4800580, 127.289039), //36.4203004, 128.317960
                position: navermaps.Position.TOP_LEFT,
                style: navermaps.ZoomControlStyle.SMALL
            },

            scaleControl: true,
            draggable: true,
            scrollWheel: true,
            bounds: null, 
        };
    }
    handleClick = (e) => {
        console.log('e.coord >>>', e.coord );
        this.setState({
            input_latlng: e.coord + ''
        });
    }
    handleToZoomLevelOne = (e) =>{
        this.setState({
            zoom: 10
        });
    }
  render() {
    return (
    <div>
        <RenderAfterNavermapsLoaded
        ncpClientId={"p8lftt0b2s"} // 자신의 네이버 계정에서 발급받은 Client ID
        error={<p>Maps Load Error</p>}
        loading={<p>Maps Loading...</p>}
        >
        <NaverMapAPI />
      </RenderAfterNavermapsLoaded>

    </div>
    );
  }
}

function NaverMapAPI() {
  return (
    <NaverMap
      mapDivId={"maps-getting-started-uncontrolled"} // default: react-naver-map
      style={{
        width: "100%", // 네이버지도 가로 길이
        height: "100%", // 네이버지도 세로 길이
      }}
      defaultCenter={{lat: 37.554722, lng: 126.970833}} // 지도 초기 위치
      defaultZoom={13} // 지도 초기 확대 배율
    />
  );
}


export default Map;
