import React from "react";
import "./App.css";

class App extends React.Component {
  render() {
    return (
      <div className="App">
        <div>
          <h1> 카 고</h1>
          <h6>주변카페 자리를 확인해보세요~</h6>
        </div>

        <div>
          <input 
          id="seearch" 
          type="search" 
          placeholder="목적지를 검색하세요" 
          onChange={this.handleChange}>
          </input>

          <input id="searchBton" type="submit" value="검색"></input>
        </div>

        <div id="App-Btn">
          <a className="App-link"
          href="./Map.js"
          target="_blank"
          rel="noopener noreferrer"
          >
          </a>현재 위치로 확인
        </div>
        {getLocation}


      </div>
    );
  }
}
/* 
getCurrentPosition:현재위치 정보 가져옴
latitude:위도
longitude:경도 */
function getLocation(){
  if(navigator.geolocation) { // GPS를 지원하는 경우
    navigator.geolocation.getCurrentPosition(function(position) { 
      alert(position.coords.latitude + ' ' + position.coords.longitude);
    }, 
    function(error) {//위치값 찾기 에러났을때
      console.error(error);
    }, 
    {//옵션
      enableHighAccuracy: false,//베터리를 소모해서 더 정확한 위치를 찾음
      maximumAge: 0,
      timeout: Infinity
    });
  } 
  else {  //GPS를 지원하지 않는 경우
    alert('GPS를 지원하지 않습니다');
  }
}

export default App;
