import React from "react";
import {BrowserRouter as Router, Route, Link} from "react-router-dom";
import "./LogIn.css";

class LogIn extends React.Component {
  handleCreate = (data) => {
    console.log(data);
  };
  render() {
    return (
      <div id="content">
        <div className="login">
          <form action="login.php" method="post">
            <LogInID onCreate={this.handleCreate} />
            <div id="pw">
              <input id="pw" placeholder="비밀번호를 입력하세요." />
            </div>

            <div id="middle">
              <input type="submit" title="로그인" Value="로그인" />
              <hr style={{marginBottom: "2%", width: "26%"}} />
            </div>

            <div id="Btn">
              <input type="submit" id="idSearchBtn" Value="아이디 찾기" />

              <input type="submit" id="pwSearchBtn" Value="비밀번호 찾기" />

              <input type="submit" id="signUpBtn" Value="회원가입" />
            </div>
          </form>
        </div>
      </div>
    );
  }
}

class LogInIDPW extends React.Component {  
  state = {
      id: "",
    };

    handleChange = (e) => {
      this.setState({
        id: e.target.value,
      });
    };
    
    handleSubmit = (e) => {
      //페이지 리로딩 방지
      e.preventDefault();
      //상태값을 oncreate를 통하여 부모에게 전달
      this.props.onCreate(this.state);
      //상태 초기화
      this.setState({
        id: "",
      });
    };
  render() {
    return (
      <div>
        <form action="login.php" method="post" onSubmit={this.handleSubmit}>
          <div id="id">
            <input
              type="text"
              id="id"
              placeholder="아이디를 입력하세요."
              value={this.state.id}
              onChange={this.handleChange}
            />
            <div>{this.state.id}</div>
          </div>
        </form>
      </div>
    );
  }
}

export default LogIn;
