import React from "react";
import "./App.css";
import {Route, Link, Router} from "react-router-dom";
import Map from "./Map";

class App extends React.Component {
  render() {
    return (
      <div className="App">
        <div>
          <h1> 카 고</h1>
          <h6>주변카페 자리를 확인해보세요~</h6>
        </div>

        <div>
          <Search />
          <input id="searchBton" type="submit" value="검색" onClick=""></input>
        </div>

        {getLocation()}
      </div>
    );
  }
}

class Search extends React.Component {
  state = {
    place: "",
  };
  handleChange = (e) => {
    this.setState({
      place: e.target.value,
    });
  };
  render() {
    return (
      <form>
        <input
        type="search"
          placeholder="목적지를 검색하세요"
          value={this.state.name}
          onChange={this.handleChange}
        ></input>
        <div>{this.state.name}</div>
      </form>
    );
  }
}

/* gPS권한 허용/차단 
getCurrentPosition:현재위치 정보 가져옴
latitude:위도
longitude:경도 
*/
function getLocation() {
  if (navigator.geolocation) {
    // GPS를 지원하는 경우
    navigator.geolocation.getCurrentPosition(
      function (position) {
        alert(position.coords.latitude + " " + position.coords.longitude);
      },
      function (error) {
        //위치값 찾기 에러났을때
        console.error(error);
      },
      {
        //옵션
        enableHighAccuracy: false, //베터리를 소모해서 더 정확한 위치를 찾음
        maximumAge: 0, //한번찾은 위치정보를 해당 초만큼 캐싱
        timeout: Infinity, //주어진 초에 찾지못하면 에러발생
      }
    );
  } else {
    //GPS를 지원하지 않는 경우
    alert("GPS를 지원하지 않습니다");
  }
}

