import React, {Component, Fragment} from "react";
import "./App.css";
import { RenderAfterNavermapsLoaded, NaverMap,Marker } from 'react-naver-maps'; // 패키지 불러오기

class Map extends React.Component {
  // map = new naver.maps.Map('mapDiv', {option});
  constructor(props) {
    super(props);
    const navermaps = window.naver.maps;

    this.state = {
      map: navermaps,
      input_latlng: "",
      zoomControl: true,
      zoom: 2,
      mapTypeId: "normal",
      center: navermaps.LatLng(36.480058, 127.289039), // 126.9861108, 37.4983439

      zoomControlOptions: {
        center: new navermaps.LatLng(36.480058, 127.289039), //36.4203004, 128.317960
        position: navermaps.Position.TOP_LEFT,
        style: navermaps.ZoomControlStyle.SMALL,
      },

      scaleControl: true,
      draggable: true,
      scrollWheel: true,
      bounds: null,
    };
  }
  handleClick = (e) => {
    console.log("e.coord >>>", e.coord);
    this.setState({
      input_latlng: e.coord + "",
    });
  };

  handleToZoomLevelOne = (e) => {
    this.setState({
      zoom: 10,
    });
  };

  render() {
    return (
      <Fragment>
        {NaverMapAPI()}
        <div>
          <NaverMap
            id="p8lftt0b2s"
            style={{width: "100%", height: "922px"}}
            onClick={this.handleClick}
            center={this.state.center}
            bounds={this.state.bounds}
          ></NaverMap>
        </div>
      </Fragment>
    );
  }
}

function NaverMapAPI() {
  const navermaps = window.naver.maps;
  return (
    <NaverMap
      mapDivId={"maps-getting-started-uncontrolled"} // default: react-naver-map
      style={{
        width: "100%", // 네이버지도 가로 길이
        height: "100%", // 네이버지도 세로 길이
      }}
      defaultCenter={{lat: 37.554722, lng: 126.970833}} // 지도 초기 위치
      defaultZoom={13} // 지도 초기 확대 배율
    >
      <Marker
        key={1}
        position={new navermaps.LatLng(37.554722, 126.970833)} 
        animation={2} 
        onClick={() => {alert('여기는 N서울타워입니다.');}}
      ></Marker>
    </NaverMap>
    
  );
}

export default Map;
