import React from "react";
import {BrowserRouter as Router, Route, Link} from "react-router-dom";
import "./LogIn.css";

class LogIn extends React.Component {
  handleCreate = (data) => {
    console.log(data);
  };
  render() {
    return (
      <div id="content">
        <div className="login">
          <form action="login.php" method="post">
            <LogInIDPW onCreate={this.handleCreate} />

            <div id="middle">
              <hr style={{marginBottom: "2%", width: "26%"}} />
            </div>

            <div id="Btn">
              <input type="submit" id="idSearchBtn" Value="아이디 찾기" />

              <input type="submit" id="pwSearchBtn" Value="비밀번호 찾기" />

              <input type="submit" id="signUpBtn" Value="회원가입" />
            </div>
          </form>
        </div>
      </div>
    );
  }
}

class LogInIDPW extends React.Component {
  state = {
    id: "",
    pW: "",
  };

  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    });
  };

  appClick = () => {
    console.log(`id는:${this.state.id}\n pw는 : ${this.state.password}`);
  };

  appKeyPress = (e) => {
    if (e.key === 'Enter') {
      this.appClick();
    }
  }

  render() {
    const {id, pw} = this.state;
    const {appChange, appClick} = this;
    return (
      <div>
        <form>
          <div id="id">
            <input
              type="text"
              id="id"
              placeholder="아이디를 입력하세요."
              value={id}
              onChange={appChange}
            />
            <div>{this.state.id}</div>
          </div>

          <div id="pw">
            <input
              type="password"
              id="pw"
              placeholder="비밀번호를 입력하세요."
            />
            <div>{this.state.pw}</div>
          </div>
          <div>
            <button onClick={appClick}>로그인</button>
          </div>
        </form>
      </div>
    );
  }
}

export default LogIn;
